/*
 * Hier sind die Bilder gespeichert, welche auf der Webseite des Sensors eingeblendet werden. Sie sind base64 codiert,
 * was z.B. über diese Webseite hier gemacht werden kann: https://www.base64-image.de/
 */

void WebseiteBildLogoFabmobil() {
    File bild = LittleFS.open("/Bilder/logoFabmobil.png", "r");
    if (!bild) {
        Serial.println("Fehler: /Bilder/logoFabmobil.png konnte nicht geöffnet werden!");
        return;
    }
    Webserver.streamFile(bild, "image/png");
    bild.close();
}

