void WebseiteCss() {
    if (!LittleFS.exists("/style.css")) {
        Serial.println("Fehler: /style.css existiert nicht!");
        return;
    }

    File css = LittleFS.open("/style.css", "r");
    if (!css) {
        Serial.println("Fehler: /style.css kann nicht geöffnet werden!");
        return;
    }
    Webserver.streamFile(css, "text/css");
    css.close();
}

void WebseiteDatenFlashAuflisten(File dir, int numTabs) {
   while (true) {
     File entry =  dir.openNextFile();
     if (! entry) {
       // no more files
       break;
     }
     for (uint8_t i=0; i<numTabs; i++) {
       Serial.print('\t');
     }
     Serial.print(entry.name());
     if (entry.isDirectory()) {
       Serial.println("/");
       WebseiteDatenFlashAuflisten(entry, numTabs+1);
     } else {
       // files have sizes, directories do not
       Serial.print("\t\t");
       Serial.println(entry.size(), DEC);
     }
     entry.close();
   }
}


